select 'Non Buku' as jenis,cast(ifnull(sum(rp.jumlah),0) as numeric(18,0))as total from rapbs r join rapbs_periode rp on r.id_rapbs = rp.id_rapbs join anggaran a on r.id_anggaran = a.id_anggaran where id_kode not in ('03.01.04.','05.01.09.','05.01.10.','05.01.12.','05.01.15.') and r.soft_delete=0 and a.soft_delete=0 and rp.soft_delete=0 and substr(r.[keterangan],1,4) = 'sisa' and a.id_ref_sumber_dana=:id_sumber_dana 
union
select 'Buku' as jenis,cast(ifnull(sum(rp.jumlah),0) as numeric(18,0))as total from rapbs r join rapbs_periode rp on r.id_rapbs = rp.id_rapbs join anggaran a on r.id_anggaran = a.id_anggaran where id_kode in ('03.01.04.','05.01.09.','05.01.10.','05.01.12.','05.01.15.')  and r.soft_delete=0 and a.soft_delete=0 and rp.soft_delete=0 and substr(r.[keterangan],1,4) = 'sisa' and a.id_ref_sumber_dana=:id_sumber_dana
