select 'Non Buku' as jenis,cast(ifnull(sum(rp.jumlah),0) as numeric(18,0))as total from  rapbs r join rapbs_periode rp on r.id_rapbs = rp.id_rapbs join anggaran a on r.id_anggaran = a.id_anggaran join ref_kode rk on r.id_ref_kode = rk.id_ref_kode where rk.uraian_kode not like '%Buku%' and r.soft_delete=0 and a.soft_delete=0 and rp.soft_delete=0 and a.id_ref_sumber_dana=:id_sumber_dana union select 'Buku' as jenis,cast(ifnull(sum(rp.jumlah),0) as numeric(18,0))as total from rapbs r join rapbs_periode rp on r.id_rapbs = rp.id_rapbs join anggaran a on r.id_anggaran = a.id_anggaran join ref_kode rk on r.id_ref_kode = rk.id_ref_kode where rk.uraian_kode like '%Buku%' and r.soft_delete=0 and a.soft_delete=0 and rp.soft_delete=0 and a.id_ref_sumber_dana=:id_sumber_dana
